SELECT * FROM (SELECT b.application_area applicationArea,b.id,b.business,b.create_by createBy,b.description,b.name, b.product,b.source,b.isArchive,b.create_time createTime,b.update_time updateTime, b.dev_process devProcess,b.orchestrator_mode orchestratorMode, b.visible visible, b.associate_git associateGit, b.data_source_list_json dataSourceListJson, GROUP_CONCAT(CONCAT(a.project_id,'-',a.priv,'-',a.username)) pusername FROM dss_project_user a LEFT JOIN dss_project b ON a.project_id = b.id AND a.workspace_id=b.workspace_id WHERE b.workspace_id = #{workspaceId} AND a.priv >=1
AND b.id = #{id}
AND b.label=#{creatorLabel}
AND b.label is null
GROUP BY b.id ) c where c.pusername like concat('%',#{username},'%') and COALESCE(c.visible,1) = 1
order by #{orderBySql}
order by updateTime desc

SELECT * FROM (SELECT b.application_area applicationArea,b.id,b.business,b.create_by createBy,b.description,b.name, b.product,b.source,b.isArchive,b.create_time createTime,b.update_time updateTime, b.dev_process devProcess,b.orchestrator_mode orchestratorMode, b.visible visible, b.associate_git associateGit, b.data_source_list_json dataSourceListJson, GROUP_CONCAT(CONCAT(a.project_id,'-',a.priv,'-',a.username)) pusername FROM dss_project_user a LEFT JOIN dss_project b ON a.project_id = b.id AND a.workspace_id=b.workspace_id WHERE b.workspace_id = #{workspaceId} AND a.priv >=1
AND b.id = #{id}
GROUP BY b.id ) c where COALESCE(c.visible,1) = 1
order by #{orderBySql}
order by updateTime desc

SELECT * FROM (SELECT b.application_area applicationArea,b.id,b.business,b.create_by createBy,b.description,b.name, b.product,b.source,b.isArchive,b.create_time createTime,b.update_time updateTime, b.dev_process devProcess,b.orchestrator_mode orchestratorMode, b.visible visible, b.data_source_list_json dataSourceListJson, GROUP_CONCAT(CONCAT(a.project_id,'-',a.priv,'-',a.username)) pusername FROM dss_project_user a LEFT JOIN dss_project b ON a.project_id = b.id AND a.workspace_id=b.workspace_id WHERE b.workspace_id = #{workspaceId} AND a.priv >=1 AND b.visible = 0
AND b.id = #{id}
GROUP BY b.id ) c where c.pusername like concat('%',#{username},'%') order by updateTime desc

select * ,group_concat(accessUser) as accessUsers ,group_concat(editUser) as editUsers ,group_concat(releaseUser) as releaseUsers from ( SELECT b.application_area applicationArea ,b.id ,b.business ,b.create_by createBy ,b.description ,b.name ,b.product ,b.source ,b.isArchive ,b.create_time createTime ,b.update_time updateTime ,b.dev_process devProcess ,b.orchestrator_mode orchestratorMode ,b.visible visible ,b.associate_git associateGit ,b.data_source_list_json dataSourceListJson ,a.workspace_id ,a.username ,case when a.priv = 1 then a.username end as accessUser ,case when a.priv = 2 then a.username end as editUser ,case when a.priv = 3 then a.username end as releaseUser FROM dss_project_user a JOIN dss_project b ON a.project_id = b.id AND a.workspace_id=b.workspace_id and b.visible = 1 WHERE a.priv >=1 and a.workspace_id = #{workspaceId}
and a.project_id = #{id}
) c where 1 = 1
and name in
#{item}
and createBy in
#{item}
and accessUser in
#{item}
and editUser in
#{item}
and releaseUser in
#{item}
group by id
having group_concat(username) like concat('%',#{queryUser},'%')
