mapper.select:
SELECT
FROM dss_onestop_menu_application m LEFT JOIN dss_application app ON m.`application_id` = app.`id`
m.`onestop_menu_id` = #{id}

SELECT
FROM dss_onestop_menu_application m LEFT JOIN dss_application app ON m.`application_id` = app.`id`
m.`onestop_menu_id` = #{id}

mapper.delete:
DELETE
FROM
dss_onestop_user_favorites
WHERE
menu_application_id = #{applicationId}
AND username = #{username}
AND workspace_id = #{workspaceId}
AND type = #{type}
